using UnityEngine;
using UnityEngine.AI;

public class EnnemiPatrouille : MonoBehaviour
{
    public Vector3 centreZone;
    public float rayonZone = 5f;
    public float tempsAttente = 1.5f;

    private NavMeshAgent agent;
    private float timer = 0f;
    private bool enAttente = false;

    void Start()
    {
        agent = GetComponent<NavMeshAgent>();
        if (agent == null) Debug.LogWarning($"{name}: NavMeshAgent manquant");
        ChoisirNouveauPoint();
    }

    void Update()
    {
        if (enAttente)
        {
            timer += Time.deltaTime;
            if (timer >= tempsAttente)
            {
                enAttente = false;
                ChoisirNouveauPoint();
            }
        }
        else if (agent != null && !agent.pathPending && agent.remainingDistance < 0.5f)
        {
            enAttente = true;
            timer = 0f;
        }
    }

    void ChoisirNouveauPoint()
    {
        Vector2 randomCircle = Random.insideUnitCircle * rayonZone;
        Vector3 destination = centreZone + new Vector3(randomCircle.x, 0, randomCircle.y);
        if (agent != null)
            agent.SetDestination(destination);
    }
}
